}

vec4 T (vec2 U) {
	return texture(iChannel0,U/iResolution.xy);
}
vec4 B (vec2 U) {
	return texture(iChannel2,U/iResolution.xy);
}
void mainImage( out vec4 Q, in vec2 U )
{	vec2 R = iResolution.xy;
 Q = vec4(0);
 float a = 2.7*iTime+4.*U.x/R.x, s = sin(a), c = cos(a);
 mat2 m = mat2(c,-s,s,c);
 vec4 w = B(U);
 //U = mix(U,w.xy,.1);
 for (float i = 0.; i < 80.; i++) {
     vec2 u = T(U).xy;
     Q +=  i*i*texture(iChannel1,U/R);
     U += 7.*u*m;

 }
 Q /= 80.*40.*40.;
 vec4 f = 0.25*(
        B (U+vec2(1,0))+
        B (U-vec2(0,1))+
        B (U+vec2(1,0))+
        B (U-vec2(0,1)));

 Q = mix(Q,f,.5);
 Q = mix(Q,w,.5);
}
