float PI = 3.1415;
float unphase = 0.5;
float rond(float offset,vec2 uv,float r,float phi) {
    uv.x -= offset ;

    uv.y -= 0.15 + tan(2.*(iTime+2.*PI*offset))/5.;

    float dis = length(uv);


    float c =  smoothstep(r,r-phi,dis);
    return c ;
}
// offset 4.*PI/3.
float RoundAndFlare(float offset,vec2 uv,float r) {
   float phi_inner = 0.005;
   float phi_outter= 0.001+ clamp(abs(sin(iTime))/10.,0.04,0.09) ;
   return 
       rond(0.15*sin(iTime+offset),uv,r,phi_inner) + 
       rond(0.15*sin(iTime+offset+ unphase),uv,r+0.1,phi_outter)/2.;

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 


    vec2 uv  = ( fragCoord -.5* iResolution.xy ) / iResolution.y;

       float phi_inner = 0.005;
   float phi_outter= 0.001+ clamp(abs(sin(iTime))/10.,0.04,0.09) ;

    float ca = RoundAndFlare(4.*PI/3.,uv,.3);
    float cb = RoundAndFlare(2.*PI/3.,uv,.3);
    float cc = RoundAndFlare(0.,uv,.3);

   vec3 outv=  vec3(ca,cb,cc);
    // float d = (sqrt(ca)+sqrt(cb)+sqrt(cc))/2.;
   float d = 1.;


    fragColor = vec4(outv *d ,1.0);
}
