#ifdef GL_ES
precision mediump float;
#endif

uniform vec2 u_resolution;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    float sinLength = 5.;
    float sinAmplitude = tan(iTime / 2.) * .05 + .25;
    float sinX = .5;
    float sinValue = sin(st.x * sinLength + iTime) * sinAmplitude + sinX;
    float smoothness = .25;

    float difference = 1. - smoothstep(sinValue - smoothness, sinValue, st.y);
    float pct = smoothstep(sinValue, sinValue + smoothness, st.y) + difference;

    pct = 1. - pct;

    vec3 color = vec3(0.);
    color = vec3(pct);
    color *= vec3(.9, st.x, st.y - .8);

    fragColor = vec4(color,1.0);
}
