/*********** Interp. with Blurred Global Illum and Direct Lighting *************/

const int RAY_STEPS = 256;
const vec3 lightPos = vec3(0., 5., -3.0);
const vec3 eyePos = vec3(0.0, 4.5, -20.0);
const vec3 refPos = vec3(0., 2.5, 0.);
const float PI = 3.14159265359;
const float FOVY = 19.5 * PI / 180.;

//controls the t-value for interpolation between lighting models
const float T_VAL = 0.85;

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

/***************** BASIC SCENE SET UP *******************/

#define FLOOR_SDF plane(pos, vec4(0, 1, 0, 2.5));
#define RED_WALL_SDF plane(pos, vec4(1, 0, 0, 5));
#define GREEN_WALL_SDF plane(pos, vec4(-1, 0, 0, 5));
#define BACK_WALL_SDF plane(pos, vec4(0, 0, -1, 5));
#define CEILING_SDF plane(pos, vec4(0, -1, 0, 7.5));
#define TALL_BOX_SDF box(rotateY(pos + vec3(2, 0, -3), 27.5 * PI / 180.), vec3(1.5, 3., 1.5));
#define SMALL_BOX_SDF box(rotateY(pos + vec3(-2, 1, -0.75), -17.5 * PI / 180.), vec3(1.5));

const int FLOOR_ID = 0;
const int RED_WALL_ID = 1;
const int GREEN_WALL_ID = 2;
const int BACK_WALL_ID = 3;
const int CEILING_ID = 4;
const int TALL_BOX_ID = 5;
const int SMALL_BOX_ID = 6;

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float plane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float sceneMap3D(vec3 pos) {
    float flr = FLOOR_SDF;
    float red_wall = RED_WALL_SDF;
    float green_wall = GREEN_WALL_SDF;
    float back_wall = BACK_WALL_SDF;
    float ceiling = CEILING_SDF;
    float tall_box = TALL_BOX_SDF;
    float small_box = SMALL_BOX_SDF;
    return min(back_wall, min(red_wall, min(green_wall, min(ceiling, min(flr, min(tall_box, small_box))))));

}

void sceneMap3D(vec3 pos, out float t, out int obj) {
    t = FLOOR_SDF;
    obj = FLOOR_ID;

    float t2 = RED_WALL_SDF
    if (t2 < t) {
        t = t2;
        obj = RED_WALL_ID;
    } 
    t2 = GREEN_WALL_SDF;
    if (t2 < t) {
        t = t2;
        obj = GREEN_WALL_ID;
    }
    t2 = BACK_WALL_SDF;
    if (t2 < t) {
        t = t2;
        obj = BACK_WALL_ID;
    }
    t2 = CEILING_SDF;
    if (t2 < t) {
        t = t2;
        obj = CEILING_ID;
    }
    t2 = TALL_BOX_SDF;
    if (t2 < t) {
        t = t2;
        obj = TALL_BOX_ID;
    }
    t2 = SMALL_BOX_SDF
    if (t2 < t) {
        t = t2;
        obj = SMALL_BOX_ID;
    }
}

/***************** RAY MARCHING LOGIC *******************/

vec3 rayCast(vec3 eye, vec3 ref, vec2 uv) {

    float len = tan(FOVY) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * iResolution.x / iResolution.y;
    vec3 p = ref + uv.x * H + uv.y * V;
    return normalize(p - eye);
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

/***************** MATERIALS *******************/

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 nor, vec3 lightDir, vec3 view) {
    float lambert = max(0., dot(lightDir, nor));
    if (hitObj == 1) { return vec3(0.63, 0.065, 0.05) * lambert; }
    if (hitObj == 2) { return vec3(0.14, 0.45, 0.091) * lambert; }

    else { return vec3(0.85, 0.81, 0.78) * lambert; }
}
/***************** BASIC FUNCTIONALITY *******************/

Intersection sdf3D(vec3 dir, vec3 eye)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj);

    vec3 isect = eye + t * dir;
    vec3 nor = normalize(computeNormal(isect));
    vec3 lightDir = normalize(lightPos - isect);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));

    return Intersection(t, surfaceColor, isect, hitObj);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    vec4 gi_image = texture(iChannel0, uv);

    //Convert to range [-1,1]
    uv = uv * 2.0 - vec2(1.);

    //vec3 eye = eyePos;
    float r = 3.;
    float x = r * cos(0.25 * iTime);
    float z = -15. + r * sin(0.25 * iTime);
    vec3 eye = vec3(x, eyePos.y, z);
    vec3 ref = refPos;

    vec3 dir = rayCast(eye, ref, uv);

    Intersection isect = sdf3D(dir, eye);
    vec4 final_color = vec4(isect.color, 1.);

    fragColor = vec4(mix(gi_image, final_color, T_VAL));   
}

/*********** Buffer A Calculates Global Illumination ****************/

const int RAY_STEPS = 256;
const vec3 lightPos = vec3(0., 5., -3.0);
const vec3 eyePos = vec3(0.0, 4.5, -20.0);
const vec3 refPos = vec3(0., 2.5, 0.);
const float PI = 3.14159265359;
const float FOVY = 19.5 * PI / 180.;
const float FOCAL_LENGTH = 23.0;
const float FOCAL_RANGE = 10.0;
const int AO_SAMPLES = 256;
const float NUM_RAYS = 50.;

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

/***************** BASIC SCENE SET UP *******************/

#define FLOOR_SDF plane(pos, vec4(0, 1, 0, 2.5));
#define RED_WALL_SDF plane(pos, vec4(1, 0, 0, 5));
#define GREEN_WALL_SDF plane(pos, vec4(-1, 0, 0, 5));
#define BACK_WALL_SDF plane(pos, vec4(0, 0, -1, 5));
#define CEILING_SDF plane(pos, vec4(0, -1, 0, 7.5));
#define TALL_BOX_SDF box(rotateY(pos + vec3(2, 0, -3), 27.5 * PI / 180.), vec3(1.5, 3., 1.5));
#define SMALL_BOX_SDF box(rotateY(pos + vec3(-2, 1, -0.75), -17.5 * PI / 180.), vec3(1.5));

const int FLOOR_ID = 0;
const int RED_WALL_ID = 1;
const int GREEN_WALL_ID = 2;
const int BACK_WALL_ID = 3;
const int CEILING_ID = 4;
const int TALL_BOX_ID = 5;
const int SMALL_BOX_ID = 6;

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float plane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float sceneMap3D(vec3 pos) {
    float flr = FLOOR_SDF;
    float red_wall = RED_WALL_SDF;
    float green_wall = GREEN_WALL_SDF;
    float back_wall = BACK_WALL_SDF;
    float ceiling = CEILING_SDF;
    float tall_box = TALL_BOX_SDF;
    float small_box = SMALL_BOX_SDF;
    return min(back_wall, min(red_wall,
     min(green_wall, min(ceiling, min(flr, min(tall_box, small_box))))));
}

void sceneMap3D(vec3 pos, out float t, out int obj) {
    t = FLOOR_SDF;
    obj = FLOOR_ID;

    float t2 = RED_WALL_SDF
    if (t2 < t) {
        t = t2;
        obj = RED_WALL_ID;
    } 
    t2 = GREEN_WALL_SDF;
    if (t2 < t) {
        t = t2;
        obj = GREEN_WALL_ID;
    }
    t2 = BACK_WALL_SDF;
    if (t2 < t) {
        t = t2;
        obj = BACK_WALL_ID;
    }
    t2 = CEILING_SDF;
    if (t2 < t) {
        t = t2;
        obj = CEILING_ID;
    }
    t2 = TALL_BOX_SDF;
    if (t2 < t) {
        t = t2;
        obj = TALL_BOX_ID;
    }
    t2 = SMALL_BOX_SDF
    if (t2 < t) {
        t = t2;
        obj = SMALL_BOX_ID;
    }
}

/***************** RAY MARCHING LOGIC *******************/

vec3 rayCast(vec3 eye, vec3 ref, vec2 uv) {

    float len = tan(FOVY) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * iResolution.x / iResolution.y;
    vec3 p = ref + uv.x * H + uv.y * V;
    return normalize(p - eye);
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

/***************** EFFECTS *******************/

void coordinateSystem(const vec3 v1, out vec3 v2, out vec3 v3) {

    if (abs(v1.x) > abs(v1.y)) {
        v2 = vec3(-v1.z, 0, v1.x) / length(v1.xz);
    }
    else {
        v2 = vec3(0, v1.z, -v1.y) / length(v1.yz);
    }
    v3 = cross(v1, v2);
}

vec2 random2(vec3 p) {
    return fract(sin(vec2(p.x, p.x + 1.0)) * vec2(43758.5453123, 22578.1459123));
}

/***************** MATERIALS *******************/

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 nor, vec3 lightDir, vec3 view) {
    float lambert = max(0., dot(lightDir, nor));
    if (hitObj == 1) { return vec3(0.63, 0.065, 0.05) * lambert; }
    if (hitObj == 2) { return vec3(0.14, 0.45, 0.091) * lambert; }

    else { return vec3(0.85, 0.81, 0.78) * lambert; }
}


/***************** LIGHTING *******************/

Intersection DirectLighting(vec3 dir, vec3 eye)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj);

    vec3 isect = eye + t * dir;
    vec3 nor = normalize(computeNormal(isect));
    vec3 lightDir = normalize(lightPos - isect);

    //compute the direct lighting for that surface
    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));

    return Intersection(t, surfaceColor, isect, hitObj);
}


Intersection GlobalIllumination(vec3 dir, vec3 eye) {

    //For each ray intersection with the scene
    float t_val;
    int hitObj;
    march(eye, dir, t_val, hitObj);
    vec3 isect = eye + t_val * dir;
    vec3 n = normalize(computeNormal(isect));

    vec3 color = vec3(0.0);
    vec3 t, b;
    coordinateSystem(n, t, b);

    //cast N rays randomly in the hemisphere
    for(float i = 0.; i < NUM_RAYS; ++i) {
        vec2 xi = random2(isect + float(i) * 203.1);
        float len = sqrt(xi.y);
        float rx = len * cos(6.2831 * xi.x);
        float ry = len * sin(6.2831 * xi.x);
        float rz = sqrt(1.0 - xi.y); // z = sqrt(1 - x*x - y*y)
        vec3 dir = vec3(rx * t + ry * b + rz * n);

        //store that for the primary ray hit
        color += DirectLighting(dir, eye).color * 3.;
    }
    //Average the N rays' contributions for each primary hit
    return Intersection(t_val, color / vec3(float(NUM_RAYS)), isect, hitObj);
}

/***************** BASIC FUNCTIONALITY *******************/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    //Convert to range [-1,1]
    uv = uv * 2.0 - vec2(1.);

    float r = 3.;
    float x = r * cos(0.25 * iTime);
    float z = -15. + r * sin(0.25 * iTime);
    vec3 eye = vec3(x, eyePos.y, z);
    vec3 ref = refPos;

    vec3 dir = rayCast(eye, ref, uv);

    Intersection isect = GlobalIllumination(dir, eye);

    // Output to screen
    fragColor = vec4(isect.color, 1.);
}

/*********** Buffer B Creates Gaussion Blur of Buffer A *************/

float normpdf(in float x, in float sigma)
{
    return 0.39894*exp(-0.5*x*x/(sigma*sigma))/sigma;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    const int mSize = 11;
    const int kSize = (mSize - 1)/2;
    float kernel[mSize];
    vec3 final_color = vec3(0.0);

    //create the 1-D kernel
    float sigma = 20.0;
    float Z = 0.0;
    for (int j = 0; j <= kSize; ++j) {
        kernel[kSize+j] = kernel[kSize-j] = normpdf(float(j), sigma);
    }

    //get the normalization factor (as the gaussian has been clamped)
    for (int j = 0; j < mSize; ++j) {
        Z += kernel[j];
    }

    //read out the texels
    for (int i=-kSize; i <= kSize; ++i) {
        for (int j=-kSize; j <= kSize; ++j) {
            final_color += kernel[kSize+j]*kernel[kSize+i] * texture(iChannel0, (fragCoord.xy+vec2(float(i),float(j))) / iResolution.xy).rgb;

        }
    }

    vec2 uv = fragCoord/iResolution.xy;
    vec4 unblurredColor = texture(iChannel0, uv);

    fragColor = vec4(final_color / (Z*Z), 1.);   
}
