#define t iTime
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/r;

    //uv.x *= r.x/r.y;

    uv -= .5; //Center's UV
    uv.x *= r.x/r.y; //Makes things nice and round.

    float d = length(uv);//UV length.

    float red; //Red declared.
    float green; //Green declared.
    float blue; //Blue declared.

    if(d < sin(t)*.01+.4) blue = 1.; 
    if(d < -cos(t*2.)*.07+.2) red = 1.;
    if(d < cos(t*60.)*.001+.05) green = 1.;

    red += d * sin(t*2.);
    blue += d * sin(t*1.5);
    green += d * sin(t*.5);

    // Output to screen
    fragColor = vec4(red,green,blue,1.0);
}
