void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy;

    vec3 col = vec3(texture(iChannel0, p.xy));
    col = clamp(1.0-sin(col*8.0), 0.0, 1.0);
    col -= length(p-0.5);

    fragColor = vec4(col, 1.0);
}

float rand(vec2 p) {
    return fract(sin(dot(p, vec2(12., 78.)))*43758.43758);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy;
    vec3 col = vec3(0.);
    vec2 eps = vec2(1.)/iResolution.xy;

    float t = mod(iTime, 1.0);
    float rt = mod(iTime, 1.1);

    if(t < 0.04 ) {
        vec2 q = vec2(p.x*iResolution.x/iResolution.y, p.y);
        vec2 id = floor(q*3.0);
        q = fract(q*3.0)-0.5;

        if(length(q) < 0.3) {
            col -= vec3(0.5, 0.4, 0.3)*10.0*rand(id+vec2(rt));
        }
    }

    vec3 lap =
         ( 0.2*sin(vec3(1.01, 1.1, 1.1)*texture(iChannel0, p + vec2(eps.x, 0.)).rgb)
         + 0.5*cos(vec3(1.1, 1.05, 1.1)*texture(iChannel0, p + vec2(0., eps.y)).rgb)
         + 0.4*sin(vec3(1.1, 1.1, 1.06)*texture(iChannel0, p + vec2(-eps.x, 0.)).rgb)
         + 0.3*cos(vec3(1.07, 1.1, 1.1)*texture(iChannel0, p + vec2(0.,  -eps.y)).rgb));

    col += lap;

    fragColor = vec4(col,1.0); 
}
