//Based on BigWings' Youtube tutorial on hex tiling. Added the wave and kaleidoscope texture sampling.
float HexDist(vec2 p){
    p = abs(p);
    float c = dot(p, normalize(vec2(1.,1.73)));
    c = max(c, p.x);
    return c;
}

vec4 HexCoords(vec2 uv) {
	vec2 r = vec2(1, 1.73);
    vec2 h = r*.5;

    vec2 a = mod(uv, r)-h;
    vec2 b = mod(uv-h, r)-h;

    vec2 gv = dot(a, a) < dot(b,b) ? a : b;

    float x = atan(gv.x, gv.y);
    float y = 0.5-HexDist(gv);
    vec2 u =vec2(sin(HexDist(uv)+iTime*10.)+uv.x,uv.y+cos(2.*iTime));
    vec2 id = uv-gv-u;
    return vec4(x, y, id.x,id.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
	uv*= 10.;
    vec4 hc = HexCoords(uv+100.);
    float c = smoothstep(.01, .03, hc.y*sin(hc.z*hc.w+iTime));

    fragColor += c;

    fragColor = (texture(iChannel0,hc.yx*sin(iTime))-c/3.2);
    fragColor.y = pow(fragColor.x, 2.+(sin(iTime*2.)));
    fragColor.x = pow(fragColor.x, 2.+(sin(iTime*4.)));
    fragColor.z = pow(fragColor.y, 2.+(sin(iTime*8.)));
}
