#define M_PI 3.1415926535897932384626433832795

float dist (vec2 A, vec2 B)
{
	return sqrt(pow(A.x - B.x, 2.0) + pow(A.y - B.y, 2.0));
}

float getTheta (float x, float y)
{
    return (atan(y,x) * 180.0/M_PI) + 180.0;
}


vec3 getColor (float r, float theta,float x,float y)
{      
    vec3 bgLight = vec3(0.7,0.7,0.8);
    vec3 bgDark = vec3 (0.45,0.45,0.6);
    vec3 bg = vec3(0.6, 0.6, 0.7);
    vec3 color;
    if (r < 150.0)
    {
	    float s = r/60.0 + 0.1*(sin(r/150.0*8.0*2.0*3.14-iTime*10.0)*sin(x*0.1));	
        color = bgDark*s + bgLight*(1.0-s);
    }
    else
    {
        color = bg;
    }

    vec3 gate = vec3(0.35, 0.35, 0.42);
    vec3 symbols = vec3(0.45, 0.45, 0.55);
    vec3 chevronsGrey = vec3(0.3, 0.3, 0.4);
    vec3 chevronsRed = vec3 (0.4,  0.25,  0.27353);


    if (r < 150.0 && r > 120.0)
    {
        color = gate;
    }
    if (r < 140.0 && r > 125.0)
    {
        float theta2 = mod(theta+iTime*10.0,9.0);
        if (theta2<8.0)
        {
          color = symbols;
        }
        else
        {
         color = gate;
        }
    }
    if (r < 152.0 && r > 130.0 )
    {
        float theta3 = mod(theta,40.0);

        float hrana1 = 0.0;
        float hrana2 = 5.0;

        float t = (r-130.0)/(152.0-130.0);
        float hrana = t*hrana1+(1.0-t)*hrana2;


        if (theta3 >hrana && theta3 < 15.0-hrana)
            {
            	color = chevronsGrey; 
            }
        if (theta3 > hrana+2.0 && theta3 < 15.0-hrana-2.0)
        {
        	color = chevronsRed;
        }

    }

    return color;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Scaling
    vec2 xy = fragCoord.xy/(iResolution.xy/vec2(800.0,450.0));

    vec2 center = vec2(800.0,450.0) /2.0;
    float dx = center.x - xy.x;
    float dy = center.y - xy.y;
    float r = dist(xy, center);
    float theta = getTheta(dx, dy);

    vec3 col = getColor(r, theta,xy.x,xy.y);

    // Output to screen
    fragColor = vec4(col,1.0);
}
