void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 circlePos = vec2(.5,.5);
    vec2 uv = fragCoord.xy/iResolution.xy;
    float aspect = iResolution.y / iResolution.x;

    vec3 col = vec3(0.);
    uv.y = uv.y * aspect;
    circlePos.y = circlePos.y * aspect;
    uv.y -= .007;

    uv.y = fract(uv.y*1.);

    float radiusOrigin = 1.5;
    float thick = .05;

    for(int i=1; i<1000; i++) {
        float radius = clamp(radiusOrigin + 0.1 * float(i) * cos((iTime/2000.) + float(i)*2.) -.8, -.1, .2);
        col += clamp(vec3(smoothstep(radius+0.001, radius,  length(circlePos-uv))), 0., 1.);
    	col -= clamp(vec3(smoothstep(radius-(thick-.001), radius-thick,  length(circlePos-uv))), 0., 1.);
    }

    uv.y += .007;

    for(int i=1; i<1000; i++) {
        float radius = clamp(radiusOrigin + 0.1 * float(i) * cos((iTime/2000.) + float(i)*2.) -.8, -.1, .2);
        col += clamp(vec3(smoothstep(radius+0.001, radius,  length(circlePos-uv))), 0., 1.);
    	col -= clamp(vec3(smoothstep(radius-(thick-.001), radius-thick,  length(circlePos-uv))), 0., 1.);
    }

    col = clamp(col, 0., 3.);
    col.r -= (sin(iTime*1.)+1.)/1.;
    col.g -= (sin(iTime*1.6)+1.)/1.;
    col.b -= (cos(iTime*1.6)+1.)/1.;

    vec2 q = uv - vec2(0.5, 0.9);
 	float r = 0.1;
    float r2 = 0.021;

    uv = fragCoord.xy/iResolution.xy;
    aspect = iResolution.y / iResolution.x;

    uv.y += -.22;
    uv.y = uv.y * aspect;
    circlePos.y = circlePos.y * aspect;
    col *= vec3(smoothstep(.04, .181, length(circlePos-uv)))*1.9;

    fragColor = vec4(col,1.0);
}
