float Circle(vec2 uv, vec2 pos, float radius, float blur){

    float dist = length(uv-pos);

    float c = smoothstep(radius,radius-blur,dist);

    return c;

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )

{
	float c = 0.;

    vec3 col = vec3(.94,.61,.03);

    // Normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;

    // account for distortion
    uv.x *= iResolution.x/iResolution.y;


    float sun = Circle(uv, vec2(-0., 0.), .4, 0.1);
	float shadow = Circle(uv, vec2(1.-(2.*mod(iTime/16.,1.)), 0.), .35, 0.05);

	c += sun;
    c -= shadow;


    //-(pow(2.*uv[0],2.)/1.))

    // Output to screen

    fragColor = vec4(c*col,0);

}
