void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xx;


    vec2 pos = iResolution.xy/2.;

    float duration = 0.4;
    float time = mod(iTime, duration);
    float radius = 2000.* time*time;
    float thickness_ratio = 0.4;

    float time_ratio = time/duration;
   	float shockwave = smoothstep(radius, radius-2.0, length(pos - fragCoord));
    shockwave *= smoothstep((radius-2.)*thickness_ratio, radius-2.0,length(pos - fragCoord));
    shockwave *= 1.-time_ratio;

    vec2 disp_dir = normalize(fragCoord-pos);


    uv += 0.02*disp_dir*shockwave;
    vec3 col = texture(iChannel0, uv).rgb;


    fragColor = vec4(col,1.0);
}
