void mainImage( out vec4 O, vec2 U )
{
    vec2 uv = U / iResolution.xy * vec2(4,2) + vec2(-2,1);
    float pi = 3.1415,
        time = iTime * .1,
           t = time*pi,
           d = length(uv)-.3;

    uv *= mat2( cos(t), -sin(t)*d*.5, sin(t), cos(t));

    float  a = atan(uv.y, uv.x),
          tt = fract(iTime) *.5 -.5,
  timeWobble = cos(t+d) *.01*d;
    float v = 0.;

    for(float j = 0.; j<10.; j+=.5){
        float q = j==0. ? tt+.5 : 1.;
        for(float i = 0.; i<6.28; i += pi/8. ){
            vec2 u = uv + vec2(sin(i),cos(i)) * (j-timeWobble+tt-.25) *q;
            v += smoothstep( .0, .05* sin(time)*(1.-d)*time, 
                             length(u)- (smoothstep(0., 3.,j+tt)+timeWobble*3.) * q );
        } 
    }
    O = .5+ .5* sin(  pi/2.* ( mod(v+iMouse, 4.) + sin(vec4(10,5,-10,0)*time) )
                     -(d*3.-a*3.) + t );
}
