//aaStep by liqwidice
float aaStep(float edge, float gradient)
{
    float halfPix = fwidth(gradient) / 2.0;
    float low = edge - halfPix;
    float hi  = edge + halfPix;
    return clamp((gradient - low) / (hi - low), 0.0, 1.0);
}

mat2 rotMat(float angle)
{
    float s = sin(angle);
    float c = cos(angle);

    mat2 rotmat = mat2(c, -s, s, c);

    return rotmat;
}
float middleSquare(vec2 uv)
{
    return aaStep(dot(abs(uv), vec2(1, 1)), 0.27);
}

float sideThingy(vec2 uv, bool mirrored)
{
    if(mirrored)
        uv *= -1.0;

    uv *= 1.2;
    uv.x += abs(sin(iTime * 0.4) * 0.2) + 0.3;
    float piece = middleSquare(uv);

    uv.x -= 0.20;
    piece -= middleSquare(uv);

    return clamp(piece, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 tex = texture(iChannel0, fragCoord / iResolution.xy).rgb;

    float ms = middleSquare(vec2(uv));       
    float sides = sideThingy(uv, false);
    sides += sideThingy(vec2(uv.x, uv.y), true);

    ms += sides;

    vec3 result = mix(vec3(ms), tex, ms);
    result = mix(result, vec3(1), 1.0 - ms);

    fragColor = vec4(result, 1.0);
}
