float ss(vec2 p,vec2 a,vec2 b) {
  return distance(
    (p-a), 
    (b-a) * clamp(dot(p-a, b-a) / dot(b-a, b-a), 0.0, 1.0));
}
void mainImage(out vec4 fragColor,in vec2 fragCoord) {
  vec2 uv = 2.0 * fragCoord.xy - iResolution.xy / iResolution.y;
  vec3 scene = vec3(0,0,0.8);
  float f = ss(uv,vec2(200,150),vec2(500,300));
  fragColor.rgb = vec3(f, 0., 0.);   
}
