vec2 N22(vec2 p)
{
	vec3 a = fract(p.xyx*vec3(123.34,234.34,345.65));
    a += dot(a, a+34.45);
    return fract(vec2(a.x*a.y, a.y*a.z));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float ratio = iResolution.x/iResolution.y;
    vec2 uv = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;

    float t = iTime*1.0+10.0;

    vec2 p1 = vec2(0.55,0.0);
    vec2 p2 = vec2(-0.55,0.0);

    float radius = 0.15;
    float expend = 0.1;
    float threshold = 0.8;
    float d = 0.0;

    for (float i = 1.0; i <= 60.0; i++)
    {
        vec2 n = N22(vec2(i+1.0,i));
        vec2 p = vec2(1.0*sin(n.x*t/ratio)*ratio, 1.0*sin(n.y*t));
        d += smoothstep(radius+expend, radius, length(uv - p));
    }
    d = smoothstep(threshold, 1.0, d);

    // Output to screen
    fragColor = vec4(0.4,0.6,1.0,1.0)*d;
}
