void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
    vec3 d;
    d.x = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(+1,0), 0).x
        - texelFetch(iChannel0, ivec2(fragCoord) + ivec2(-1,0), 0).x;
    d.y = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(0,+1), 0).x
        - texelFetch(iChannel0, ivec2(fragCoord) + ivec2(0,-1), 0).x;
    d.z = 0.1;
    vec3 n = normalize(d);
    //vec3 l = normalize(vec3(1.0, 0.0, 1.0));
    vec3 l = normalize(vec3(cos(iTime), sin(iTime), 1.0));
    //fragColor = vec4(0.5 + 0.5 * n, 1.0);
    //fragColor = vec4(dot(n, l)) * texture(iChannel1, fragCoord/iResolution.xy);
    float h = texelFetch(iChannel0, ivec2(fragCoord), 0).x;
    fragColor = dot(n, l) * mix(
        texture(iChannel1, fragCoord/iResolution.xy),
        texture(iChannel2, fragCoord/iResolution.xy),
        h
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iTime < 1.0)
        fragColor = vec4(0);
    else
    {
        float c = texelFetch(iChannel0, ivec2(fragCoord), 0).x;
        float d = 20.0 * length(iMouse.xy - fragCoord)/iResolution.y;
        float e = exp(-d*d);
        fragColor = vec4(1.0 - (1.0 - c) * (1.0 - e));
    }
}
