float func(float lhs, float rhs, vec2 w)
{
    return (w.x*(lhs+rhs)+w.y*(lhs-rhs))/(w.x+w.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float majorAxis = max(iResolution.x, iResolution.y);
    vec2 uv = fragCoord/majorAxis;
    vec2 center = iResolution.xy/2.0/majorAxis;
    vec2 disp = abs(uv-center)*2.0+vec2(.01,.01);

    vec2 freq = texture(iChannel0, vec2(pow(max(disp.x,disp.y), 1.5), 0.0)).xy;
    vec2 adjFreq = pow(pow(freq, vec2(10.0))*10.0, vec2(2.0));

    vec2 w = .95*vec2(
        adjFreq.x,
       	1.0-adjFreq.x
    )+.05*vec2(1.0);

    float f1 = func(disp.x, disp.y, w);
    float f2 = func(disp.y, disp.x, w);

    vec3 col = fract(vec3(
        .9*f1+.1*f2,
        .5*(f2),
        1.0-(.9*f1+.1*f2)
    ));

    //col = vec3(adjFreq.x);

    // Output to screen
    fragColor = vec4(col,1.0);
}
