vec2 toPolar(vec2 p) {
	return vec2(length(p), atan(p.y, p.x));     
}

vec2 fromPolar(vec2 p) {
	return vec2(p.x * cos(p.y), p.x * sin(p.y));    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.0);
	vec2 pv = toPolar(uv * 0.5 + 0.5);
    float t = sin(iTime) * 0.5 + 0.5;
  	pv.x += 0.01 * (
        cos(1./t * pv.y * uv.x * uv.y * pv.x)

    );

    uv = fromPolar(pv) * 2.0 - 1.0;
    float d = length(pv.x);

    col = texture(iChannel0, uv).rgb;
    // Output to screen
    fragColor = vec4(col,1.0);
}
