#define S(a, b, c) smoothstep(a, b, c)

float onda(vec2 uv)
  {
    float ondulacion = 0.;
    float freq = 10.;
    for (float i = 1.; i < freq; i += 1.){
      ondulacion += .4 / i * sin(uv.x  * i * i );
    }

   return ondulacion;
}

float dibuja_capas(vec2 uv, float num_capa)
  {
    float separacion = 0.02;
    float altura = 0.8 - separacion * num_capa;
    float capa_alpha = ((uv.y - altura) * 8.)  + onda(uv + num_capa);
    capa_alpha = S(0.3, 0.1, clamp(capa_alpha, 0., 1.));

    return capa_alpha * separacion * 1.4;
  }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord / iResolution.xy;
  float color = 0.;
  float capas = 40.;
  for (float i = 0.; i < capas; i += 1.)
    {
      color += dibuja_capas(uv, i);
      uv.x += iTime * .002;
    }

  fragColor = vec4(vec3(color), 1.);
}
