// variant of https://shadertoy.com/view/wsVyzm

#define D(c,z)                                      /* --- dune( yellowness, depth ) */  \
   v = 1.-sqrt(.5+.5*sin(10.*U.x-5.*U.y));                    /* dune shape equation */  \
   O -= clamp(1. - abs(.6+.15*v-z-U.y-1./R.y)/fwidth(.15*v-U.y), 0., 1.); /* outline */  \
   O = mix( O, C= vec4(1,c+ z+U.y-.5,0,1), clamp( R.y/2.*( .6+.15*v-z - U.y)  , 0.,1.) ) \
     + .6* C *(1.-v)* max(texture(iChannel0, U/vec2(7,1) -vec2(.03*iTime/(.6-z),0)) +.6-1.5*(U.y+z),0.); \
   U *= .6; U.x -= .6;

void mainImage(out vec4 O,vec2 u){
    vec2 R = iResolution.xy, 
         U = 1.7* u / R.y; 
    vec4 C; float v;
    O = vec4(1,1,.8,1); // yellow sky
 // O = vec4(.8,1,1,1); // cyan sky
    D(.3,-.52)
    D(.5, 0.)
    D(.7,.32)
    D(.8,.52)
}
