vec4 hash42(vec2 p)
{
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2.*fragCoord - iResolution.xy ) / iResolution.y;

	float len = length(uv);
    float len2= length(uv)+.5;

 float ring =smoothstep(80.0*(sin(iTime*4.)+1.1)/3./iResolution.y, 0.0, abs(len-(tan((iTime))+1.)/3.-.5));
    ring+= smoothstep(80.0*(cos(iTime*4.)+1.1)/3./iResolution.y, 0.0, abs(len2-(sin((iTime))+1.)/3.-.5));


    fragColor = vec4(ring) + 0.20*cos(iTime/2.-uv.yxyx+vec4(0,2,4,0)); // add color 
	fragColor+=(hash42(uv*1e3+iTime*.2)-.5)*.2;  // optional film grain effect from https://www.shadertoy.com/view/tdy3DD
    //fragColor=vec4(fragColor.x);//monochrome option
    //fragColor = vec4(smoothstep(4.0/iResolution.y, 0.0, abs(length(uv)-.6))); //original non-moving ring
}
