void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float speed = 0.5;
    float depth = 75.0;
    // Normalized pixel coordinates
    float depthFactor = ((abs(sin(iTime*speed)))*depth);
    vec2 uv = ((fragCoord/iResolution.xy)-0.5)*depthFactor;

    int isX = int(mod(uv.x,2.0));
    int isY = int(mod(uv.y,2.0));
    //float col = float((isX^isY)&1);		// Later version of GLES can handle bitwise operators
	float col = (isX == isY) ? 0.0 : 1.0;	// Failsafe for older versions

    // Output to screen
    fragColor = vec4(col,col,col,1.0);
}
