void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 xy=fragCoord/iResolution.xy;
   float blursize=10.;
   float range=floor(blursize/2.);
    vec4 color=vec4(0.);

    for(float a=-range;a<=range;++a)
    for(float b=-range;b<=range;++b)
      color+=texture(iChannel0,xy+vec2(a/iResolution.x,b/iResolution.y));
      color=color/pow(blursize,2.);
      fragColor=color;  

}
