const float count = 10.0;
const float str = 0.6;
const float size = 0.2;
const float soft = 0.05;

float random(vec2 index)
{
 	return  fract(sin(dot(index, vec2(12.98,1235.0)))*4378.54);
}

vec2 random(vec4 index)
{
    vec2 x = vec2(dot(index.xy,vec2(12.98,1235.0)),dot(index.zw,vec2(12.98,1235.0)));
    return fract(sin(x)*4378.54);
}

vec2 noise(vec2 index)
{
    vec2 noi = vec2(random(index),random(index*2.0));
	return noi - vec2(0.5);
}

vec4 noise(vec4 index)
{
    vec4 noi = vec4(random(index),random(index*2.0));
    return noi.xzyw- vec4(0.5);
}

float disk(vec2 uv, vec2 position)
{
    return distance(uv,position);
}

float mySmoothstep(float x,float position,float softness)
{
    return smoothstep(position-softness,position+softness,x);
}

vec2 mySmoothstep(vec2 xy,float position,float softness)
{
 	return smoothstep(position-softness,position+softness,xy);
}

float saturate(float x)
{
    return clamp(x,0.0,1.0);
}

float calDisk(vec2 uv, vec2 dir, vec4 offset)
{

    vec2 dis;
    dis.x = disk(uv + dir,offset.xy);
    dis.y = disk(uv - dir,offset.zw);

    vec2 t;
    t = mySmoothstep(dis,size,soft);
    return (1.0-t.x)+( 1.0-t.y);
}

float calDisk(vec2 uv, vec2 offset)
{
 	float dis;
    float t;
    dis = disk(uv,offset);
    t = mySmoothstep(dis,size,soft);
    return 1.0-t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv *= count;
	vec2 index = floor(uv);
    uv = fract(uv);
    uv -= vec2(0.5);

    vec2 n_0 = noise(index) * str;
    vec2 n_11 = noise(index-vec2(1.0,0.0));

    vec4 rl = vec4(index-vec2(1.0,0.0),index + vec2(1.0,0.0));
    vec4 ud = vec4(index-vec2(0.0,1.0),index + vec2(0.0,1.0));
    vec4 rc = vec4(index-vec2(-1.0,-1.0),index + vec2(-1.0,-1.0));
    vec4 lc = vec4(index-vec2(1.0,-1.0),index + vec2(1.0,-1.0));

    vec4 n_1 = noise(rl) * str;//right and left offset with random
    vec4 n_2 = noise(ud) * str;//under and down offset with random
    vec4 n_3 = noise(rc) * str;
    vec4 n_4 = noise(lc) * str;

    float t0 = calDisk(uv,n_0);
    float t1 = calDisk(uv,vec2(1.0,0.0),n_1);
    float t2 = calDisk(uv,vec2(0.0,1.0),n_2);
    float t3 = calDisk(uv,vec2(-1.0,-1.0),n_3);
    float t4 = calDisk(uv,vec2(1.0,-1.0),n_4);

    //fragColor = vec4(n_0.xy,0.0,0.0);
	fragColor = vec4(t1+t0+t2+t3+t4);
}
