#define PIXEL_SIZE 3

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pseudoResolution = floor(iResolution.xy / float(PIXEL_SIZE));
    vec2 pseudoPixel = floor(fragCoord.xy / float(PIXEL_SIZE));
    vec2 pseudoUv = pseudoPixel / pseudoResolution;

    vec3 tex = texture(iChannel0, pseudoUv).xyz;
    float bw = (tex.x + tex.y + tex.z) / 3.;
    float col = 0.;

    if(bw > 0.8) {
        col = 1.;
    } else if(bw > 0.6) {
        col = float(!(int(pseudoPixel.x) % 2 == 0 && int(pseudoPixel.y) % 2 != 0));
    } else if(bw > 0.4) {
        col = float((int(pseudoPixel.x) % 2 + int(pseudoPixel.y) % 2) % 2 == 0);
    } else if(bw > 0.2) {
        col = float(int(pseudoPixel.x) % 2 == 0 && int(pseudoPixel.y) % 2 == 0);
    }

    fragColor = vec4(col, col, col, 1.);
}
