void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	const float pi = 3.1415926535;
    vec2 p = fragCoord.xy - iResolution.xy / 2.0;
    float phi = atan(p.y, p.x );

    float ks =sin((iTime/17.))*13.;
    float fin = floor(phi *ks / pi + 0.5);
    float phi_fin = fin * pi / ks;

    vec2 dir = vec2(cos(phi_fin), sin(phi_fin));
    float l = dot(dir, p) ;

    float ivr = 13.0;
    float seg = l / ivr;

    float w = sin(floor(seg) * 0.2 - iTime) * 0.4 + 0.5;
    float c = (w / 2.0 - abs(fract(seg) - 0.5)) * ivr;

    fragColor = vec4(c*sin(ks/2.), c*0.1, 0.5*c, 1);
}
