#define PI 3.141592

float random1(float p) { return fract(sin(p * 127.1) * 437.53) * 2.0 - 1.0; }

float random3(vec3 p) {
  return fract(sin(dot(p, vec3(127.1, 311.7, 231.4))) * 437.53) * 2.0 - 1.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 p =
      vec2(fragCoord.x/iResolution.x,-fragCoord.y/iResolution.y) ;
    p.x += mix(random3(vec3(p.x - mod(p.x + 1.0, random1(p.y)), p.y, iTime)), 0.0,
                   0.999 + exp(-30.5 * random1(iTime - mod(iTime, 0.01))));
  vec3 color = texture(iChannel0, vec2(p.x, -p.y)).rgb;
  fragColor = vec4(color, 1.0);
}
