float Circle(vec2 uv,vec2 p,float r,float blur){

    float d=length(uv-p);

    float c=smoothstep(r,r-blur,d);
    return c;
}

float smileyFace(vec2 uv, vec2 p,float size){

    	uv-=p;
    	uv/=size;
    	float c=Circle(uv,vec2(0.0,0.0),0.4,0.02);
        c-=Circle(uv,vec2(-0.15,0.1),0.06,0.02);
        c-=Circle(uv,vec2(0.15,0.1),0.06,0.02);
        c+=Circle(uv,vec2(0.15,0.1),0.03,0.02);
        c+=Circle(uv,vec2(-0.15,0.1),0.03,0.02);

        float mouth=Circle(uv,vec2(0.0,-0.2),0.2,0.02);
         mouth-=Circle(uv,vec2(0.0,-0.25),0.2,0.02);
   		 c-=mouth;
    return c;
}
float Band(float t,float start,float end,float blur ){
	float step1=smoothstep(start-blur,start+blur,t);
	float step2=smoothstep(end+blur,end-blur,t);
    return step1*step2;
}

float Rect(float x, float left,float right,float blur){
    float band1=Band(x,left,right,blur);

    return band1;


}
