// Images added with custom chrome extension 
// https://s3-us-west-2.amazonaws.com/s.cdpn.io/123024/leopard2
// https://s3-us-west-2.amazonaws.com/s.cdpn.io/123024/lion2.

#define PI 3.141592653589793
float sineInOut(float t) {
  return -0.5 * (cos(PI * t) - 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

            vec2 uv = fragCoord/iResolution.xy;
            vec4 _currentImage;
            vec4 _nextImage;
        // Progress of the transition
    		float dispFactor = sineInOut(iTime * 2.);
    // dispFactor = iMouse.x/iResolution.x;
    		// How much the red color of the image affects the displacement
            float intensity = 0.3;

    		// Get the image colors
            vec4 orig1 = texture(iChannel0,uv);
            vec4 orig2 = texture(iChannel1,uv);
            // orig2 = vec4(1.);

    		// Distort/displace the image based on the red color of the other image
            // Thats why it looks that way, because the red value varies 
    		// And if you use another image the displacement will look diferent
            _currentImage = texture(iChannel0, 
                              vec2(uv.x, uv.y + dispFactor * (orig2.r * intensity)));
			// And this is the same but backwards
            _nextImage = texture(iChannel1, vec2(uv.x,
                            uv.y + (1.0 - dispFactor) * (orig1.r * intensity)));

            vec4 finalTexture = mix(_currentImage, _nextImage, dispFactor);

    		// Uncomment to see how it looks
    		//finalTexture= _nextImage;
    		//finalTexture= _currentImage;

            fragColor = finalTexture;
}
