#define lim 20.

float shape(vec2 z)
{
	float t = iTime;
	z *= mat2(cos(t),-sin(t),sin(t), cos(t));
	//float _dot = dot(z,z);
	float _quad = max(abs(z.x),abs(z.y));
	float _tri = max(abs(z.x)+z.y,-z.y);
	return abs(mix(_quad, _tri, 0.5))+17.;
}

void mainImage( out vec4 f, vec2 z )
{
    f.xyz=iResolution;
    z = (z+z-f.xy)/f.y;
    vec2 g = z-z; // julia

    if (iMouse.z > 0.) g = z; // mandelbrot

   	z -= g;
    float it = 1.;
    for (float i=0.;i<23.;i++)
    {	
        z = mat2(z,-z.y,z.x) * z + g -
    		(iMouse.z>0. ? 
            	 vec2(0.5,0.) : // center mandelbrot
            	 vec2(1,.3)); // julia

        if (shape(z) > lim)
        {
            it += i;
            break;
        }
	}

	float l = log(log(length(z))/log(sqrt(lim)))*0.8;

	f.rgb = cos(l+vec3(0.636,0.978,0.598)*
                // use ceil if you wnat keep pink color
                floor(it*(sin(iTime)*.5+.5))); 


}
