void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy;
    xy.x = xy.x / iResolution.x;
    xy.y = xy.y / iResolution.y;
    vec4 color = vec4(1.0,0.0,0.0,1.0);
    if(xy.y > 0.33){
        color.b = 1.0;
        color.r = 0.0;
    }
    if(xy.y > 0.66){
        color.g = 1.0;
        color.r = 1.0;
    }
    fragColor = color;
}
