const float lines = 16.0;


float hash1( uint n ) 
{
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return float( n & uvec3(0x7fffffffU))/float(0x7fffffff);
}

float changingLetter(vec2 uv, float t)
{
    return texture(iChannel0, uv/lines+1.0/lines*floor(t)).r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x/iResolution.y;


    float t0 = iTime + 10.0;
    uv = vec2(uv.x*ratio, uv.y)*50.0;
    uint id = uint(floor(uv.x));
    float speed = hash1(id);
    float t = t0*5.0*speed;
    uv.y += t;
    float t1 = t0*8.0*hash1(uint(floor(uv.x)+floor(uv.y)));
    vec2 gv = fract(uv);

    float value = changingLetter(gv, t1)*(speed*0.9+0.1);

    vec3 col = mix(vec3(0.0, 0.0, 0.0), vec3(0.0, 0.9, 0.0), value);

    fragColor = vec4(col, 1.0);
}
