#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texelFetch(iChannel3,ivec2(U),0)
#define Main void mainImage (out vec4 Q, vec2 U) 
#define Init if (iFrame < 1) 
#define Border if (U.x<1.||R.x-U.x<1.||U.y<1.||R.y-U.y<1.)
#define T(U,t) texelFetch((t),ivec2(U),0)
#define f(U,t) length(T((U),(t)).xyz)
#define I 11
#define Neighborhood vec4 n = A(U+vec2(0,1)), e = A(U+vec2(1,0)), s = A(U-vec2(0,1)), w = A(U-vec2(1,0)), m = 0.25*(n+e+s+w);

vec2 convo (vec2 U, sampler2D t) {
	vec2 r = vec2(0); float d = 0., m=f(U,t);
    for (int x = -4;x<=4;x++)
        for (int y = -4;y<=4;y++) {
        	vec2 v = vec2(x,y);
            float z = f(U+v,t);
            float e = exp(-.01*dot(v,v));
            r += (z-m)*v*e;
            d += (z-m)*e;
        }
    return vec2(.04*length(r),0.5+.06*(d));
}
// Dave H
vec2 hash23(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

Main {
    U =0.5*iChannelResolution[3].xy+0.5*U*iChannelResolution[3].x/R.x;
    Q.xy = convo(U,iChannel3);
    Q.zw = U;
}

void X(inout vec4 Q, vec2 U, vec4 n) {
	if (length(U-n.xy*R)<length(U-Q.xy*R))
        Q = n;
}
Main {
	if (iFrame%I==0) Q=A(U);
    else {
    	Q = B(U);
        float k = exp2(float(I-1-iFrame%I));
        X(Q,U,B(U+vec2(0,k)));
        X(Q,U,B(U+vec2(k,0)));
        X(Q,U,B(U-vec2(0,k)));
        X(Q,U,B(U-vec2(k,0)));
    }
}

Main {
	Q = C(U);
    vec4 b = B(U);
    float e = 1./(1.+0.1*length(U-b.xy*R));
    if (e>Q.w){
    	Q = D(b.zw);
        Q *= Q.w;
        Q.w = e;
    }

    if (iFrame < 1) Q = vec4(0);

}

Main {
	Q = A(U);

    vec2 co = convo(U,iChannel0);
    vec4 color = C(co*R);
    for (int x = -2; x<=2; x++) 
        for (int y = -2; y<=2; y++) {
            vec4 z = C(co*R+2.*vec2(x,y));
        	if (length(color.xyz-z.xyz)>length(color.xyz-Q.xyz))
                color = z;
        }
    vec4 dQ = color-Q;
    Neighborhood;
   	Q += (1.-Q.w)*(m-Q);
    if (length(dQ)>0.)
    Q += 0.006*sign(dQ)*(color.w*color.w);
    Q = mix(Q,D(U),
            0.005);
    Q.w = color.w;

    if ((iMouse.z>0.&&length(U-iMouse.xy)<20.)||iFrame < 10) {
    	Q = D(U);
        Q.w=0.;
    }
}
