float circle(vec2 uv, float radius, vec2 shift, float blur)
{
	float circle = length(uv - shift);
    float result = smoothstep(radius, radius - blur, circle);

    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    float animationSpeed = 30.;
    float x = -0.3 + iTime / animationSpeed;
    float a = circle(uv, 0.3, vec2(x * 2., x*x), 0.015);
    a -= circle(uv, 0.31, vec2(iTime / animationSpeed + x * 2., x*x), 0.02);

    // Output to screen
    fragColor = vec4(vec3(a * 0.9), 1);
}
