void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y; //Vertically -1 to +1
    uv /= vec2(0.95); //Scale



    vec3 col = mix(
        vec3(mix(vec3(1.,1.,0.), vec3(0.,0.,1.), uv.x+.5)),
        vec3(mix(vec3(0.,1.,0.), vec3(1.,0.,0.), uv.y+.5)),
        cos(iTime*0.5)/2.+.5
    );



    // Output to screen
    if (abs(uv.x) > 1.0 || abs(uv.y) > 1.0) {
        col = vec3(0);
    }
    fragColor = vec4(col,1.0);
}
