void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const int n = 20;
    vec2 x[n];
    for(int i=0;i<n;++i)
    	x[i] = vec2(0.5+sin(iTime*(float(i)+1.0)*0.1)*0.7,0.5 +cos(iTime*(float(i)+3.0)*0.3)*0.3);
    vec2 uv = fragCoord/iResolution.y;
    int closest=-1; float dist;
    for(int i=0;i<n;++i)
    {
        float candidatedist = distance(uv,x[i]);
        if(closest==-1 || candidatedist<dist)
        {
            closest = i;
            dist = candidatedist;
        }
    }
    float prox0 = sin(dist*20.0)*0.1+0.3;
    float prox1 = cos(dist*45.0)*0.1+0.3;
    fragColor=vec4(prox0*0.7,prox1*0.8,prox0*1.4,1);
}
