vec4 convertToUYUV(vec4 inputColor )
{
	mat4 RGBtoYUV = mat4(0.257,  0.439, -0.148, 0.0,
    	                 0.504, -0.368, -0.291, 0.0,
        	             0.098, -0.071,  0.439, 0.0,
            	         0.0625, 0.500,  0.500, 1.0 );
    return RGBtoYUV * inputColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec4 wantedColor =  vec4( 1.0, .0, .0, 1. );
    //Rvec4 wantedColor =  vec4( .0, 1.0, .0, 1. );

	vec2 uv = fragCoord.xy / iResolution.xy;
  	vec4 color = texture(iChannel0, uv);
 	float grey = dot(vec3( color), vec3(0.299, 0.587, 0.114) );
    vec4 greyColor = vec4(vec3(grey), 1.);

	vec4 wantedColor_YUV =  convertToUYUV( wantedColor );
    vec4 input_color_YUV = convertToUYUV( color );

    float chromaDist = distance( wantedColor_YUV.gb, input_color_YUV.gb );

    float threshold = .3;
    float threshold_high = threshold + .1;

    float boost = .1f;
    color += dot( color, wantedColor) * wantedColor * boost; // Boost wanted color

    fragColor = mix( greyColor, color,  smoothstep( threshold_high , threshold, chromaDist) );
}
