vec2 hexagon(vec2 uv){
    uv *= vec2(0.577350278, 1.0); // hexagonal ratio
    float z = clamp(abs(mod(uv.x+floor(uv.y), 2.0)-1.0)*3.141592653-1.047197551, 0.0, 1.0);
    uv.y = floor(uv.y + z);
    uv.x = (floor(uv.x*0.5 + mod(uv.y, 2.0)*0.5) - mod(uv.y, 2.0)*0.5 + 0.5)*3.464101665; // convert back from hexagonal ratio
    return uv;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	// Normalized pixel coordinates (from 0 to 1)
	vec2 uv = fragCoord / iResolution.y; // square UV map
    vec2 c = iResolution.xy*0.5/iResolution.y; // normalised center
	vec2 r = vec2(iResolution.y/iResolution.x, 1.0); // skew the square UV map to fill image sources to the screen

	// Settings
	float count = 32.0; // This is measured to the 1.0 boundary, and it's actually half this because math reasons :shrug:
    float antialias = 0.075; // The distance between 0.0-1.0 where the edge will be blured, and it's actually twice this
	// Texture
	vec4 tex = texture(iChannel0, hexagon(uv*count)/count*r);

	// Output to screen
    fragColor = vec4(tex.rgb, 1.0);
}
