float Hash21(vec2 p){
	p = fract(p*vec2(235.12, 546.62));
    p += dot (p, p+34.23);
    return fract(p.x * p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 20.;
    vec3 col = vec3(0);
    vec2 gv = fract(uv);
    vec2 id = floor(uv);

    float randPos = Hash21(id);
    float time = iTime * (-.5-randPos);
    float timeId = floor(time);
    float timeSign = sign(fract(timeId*.5)-.5);

    float d = length(abs(gv-0.5));
    float innerDiameter = 0.0;
    float outterDiameter = 0.2;

    float mask = smoothstep(.02, -.02, d-.5);

    float movingD = fract(d + time * timeSign);

    if (movingD> innerDiameter && movingD < outterDiameter && d <.5) 
        col = vec3((fract(randPos * 123.46 + fragCoord.x * 100.)*.8+.2) * mask);

    fragColor = vec4(col,1.0);
}
