#define COLOR vec3(1.1, 1.0, 1.1)

void mainImage( out vec4 OUT, in vec2 fragCoord )
{
	vec2 point=(2.0*fragCoord.xy-iResolution.xy)/max(iResolution.x,iResolution.y);
	for(int i=1;i<11;i++)
	{
		vec2 iter=point;
		iter.x+=2.5/float(i)*sin(float(i)*point.y+(iTime/3.0)+0.3*float(i));
		iter.y+=1.6/float(i)*cos(float(i)*point.x+(iTime/4.0)+0.3*float(i));

		point=iter;
	}
	vec3 col=vec3(1.0 - abs(cos(point.x)), 1.0 - abs(cos(point.x+point.y)), 1.0 - cos(sin(point.y)))
        			*   COLOR;

	 OUT = vec4(col, 1.0);
}
