void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2.*fragCoord - iResolution.xy ) / iResolution.y;

	float len = length(uv);
    float len2= length(uv)+.5;

 float ring =smoothstep(80.0*(sin(iTime*4.)+1.1)/3./iResolution.y, 0.0, abs(len-(tan((iTime))+1.)/3.-.5));
    ring+= smoothstep(80.0*(cos(iTime*4.)+1.1)/3./iResolution.y, 0.0, abs(len2-(sin((iTime))+1.)/3.-.5));

    fragColor = vec4(ring)+.0;
     //fragColor = vec4(smoothstep(4.0/iResolution.y, 0.0, abs(length(uv)-.6))); //original non-moving ring
}
