mat2 rot( float a, vec2 uv ) {

    if ( (uv.x < 0.5 && uv.y < 0.5) || (uv.x > 0.5 && uv.y > 0.5) ) {
      a = -a;
    }

    return mat2( cos(a),-sin(a),sin(a),cos(a));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv *= rot(iTime, uv);
   uv += 0.5;
    uv = abs( 2. * uv - 1.);
   uv = fract( (4. * (mod(uv.x, 2.) +1.)* uv));
    //uv = fract(2. * uv);

    uv *= rot( mod( uv.y, 2.), uv);



    vec3 col = vec3(0.);
    col.r += uv.y * step( 0.9, distance(uv,vec2(sin(iTime))));


    vec2 a = vec2( uv.x );
    vec2 b = vec2(uv.y);
    vec2 c = uv.yx;
    float mind = 10000. ;
    mind = min( mind, distance(uv,a));
     mind = min( mind, distance(uv,b));
     mind = min( mind, distance(uv,c));

    col.b = mind;

     vec2 a2 = vec2( 0.2 );
    vec2 b2 = vec2(0.5);
    vec2 c2 = vec2(0.7);
    float mind2 = 10000. ;
    mind2 = min( mind2, distance(uv,a2));
     mind2 = min( mind2, distance(uv,b2));
     mind2 = min( mind2, distance(uv,c2));

    col.g = mind2;

    col *= 1.4 ;



    fragColor = vec4(col,1.0);
}
