vec2 u_position = vec2(10.);
float u_radius = 160.;

float variations = 100.;
float speed = 8.;


float random(vec2 st) { return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.153); }

float quitic(float x) { return x * x * x * (x * (x * 6. - 15.) + 10.); }
float hermite(float x) { return x * x * (3.0 - 2.0 * x); }

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = vec2(fragCoord.x, iResolution.y - fragCoord.y);
	float time_seed = floor(variations * fract(iTime * speed / variations)) / variations;

    vec4 clear = vec4(0., 0., 0., 0.);
    vec4 back = vec4(0., 0., 0., 1.);
    vec4 white = vec4(1., 1., 1., 1.);
    vec4 red = vec4(1., 0, 0, 1.);
    vec4 green = vec4(0, 1., 0, 1.);
    vec4 blue = vec4(0, 0, 1., 1.);

    vec4 orange = vec4(227. / 255., 102. / 255., 68. / 255., 1.);

    // Cell colors
    const int COLOR_COUNT = 4;
    vec4 color_pool[COLOR_COUNT];
    color_pool[0] = vec4(66. / 255., 118. / 255., 118. / 255., 1.);
    color_pool[1] = vec4(63. / 255., 157. / 255., 130. / 255., 1.);
    color_pool[2] = vec4(161. / 255., 205. / 255., 115. / 255., 1.);
    color_pool[3] = vec4(236. / 255., 219. / 255., 96. / 255., 1.);

    vec4 color = clear;

    float dst = 0.0;
    dst = distance(st, u_position + u_radius);

    float w = dst / u_radius;
    w = float(w < random(st + time_seed + 3.));

    int color_index = int(mod(random(st + time_seed + 4.) * 10., float(COLOR_COUNT)));
    color += mix(clear, color_pool[color_index], step(1.0, w));

    fragColor = color;
}
