void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord/iResolution.xy;
    float size=20.;
    vec2 pixel_size=vec2(size/iResolution.x,size/iResolution.y);

    vec2 position=floor(xy/pixel_size)*pixel_size;
    vec4 texColor=texture(iChannel0,position);
    fragColor=texColor;
}
