int N = 40;        // N×N samples
#define hash2(p)   fract( sin( (p) * mat2(127.1,311.7, 269.5,183.3) ) *43758.5453123 ) // https://www.shadertoy.com/view/llySRh
#define hue(v)    ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )                     // https://www.shadertoy.com/view/llySRh

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 2.*u - R, V;

    float l = 1e5, d;
    int i=0, k, T = int(iTime)%3;
    for( ; i<N*N; i++ ) {
        vec2 H = hash2(vec2(i%N,i/N)+iTime), P; 
        float r = H.x, a = 6.283*H.y;                   // use as polar coord
        r = U.x < 0. ? sqrt(r)                          // left:  uniform
                     : sqrt(-2.*log(r)) /3.;            // right: gaussian ( sigma = 1/3 to fit the screen )

        P = r * cos(a+ vec2(0,11) );                    // disk sample
        P = U - R.y*P;
     // l = min(l, dot(P,P));                           // closest sample
        d = dot(P,P); 
        if (d<l) l=d, V=P, k=i;
    }
    O = T == 1 ? vec4 ( 1.5 - .5*sqrt(l) )              // draw sample closest to pixel
      : T == 2 ? hue(float(k)/float(N*N))               // draw voronoï with sample id = col
   // : T == 2 ? vec4(float(k)/float(N*N))              // draw voronoï with sample id = grey
      :          texture(iChannel0, .5+.5* V*10./R.y ); // splat Britney on sample

}
