void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Kerze zeichnen 
    // 1. senkrechter Balken
    float c = smoothstep(0.13,0.10,abs(0.5 - uv.x)); 
    // 2. begrensen in der HÃ¶he
    c = c *  smoothstep(0.6,0.59,abs(uv.y)); 

    // Mit dem Hintergrund verschmelzen
    col += vec3(c*2.0,c*2.0,c*2.0);

    // Flamme
    float f = smoothstep(0.04,0.00,sin(uv.y*12.0+2.1)*0.02 + abs((0.5+sin(uv.y*9.1+iTime )*0.01)- uv.x)); 

    col += vec3(f*1.0,f*2.0,f*0.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
