// full diffusion variant of https://shadertoy.com/view/tsyBRV
// variant of https://shadertoy.com/view/wdKBWm

void mainImage( out vec4 O, vec2 u )
{
    O = T(u);
    float v = O.b;  // try .g for 8-connexity
    O = v > 0.    
          ? .6 + .6 * cos( v/20.  + vec4(0,23,21,0) ) // https://www.shadertoy.com/view/llySRh
          : O.wwww; // substrate (draw where not yet colored by percolation )
}

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = u / R,
          P = ( u + .5*vec2(iFrame,0) );
    if (iMouse.z>0.) P /= 2.;                                 // zoom pattern
    float g = 1.5 + .25*U.y, v;
 // float g = 2.4 + .25*U.y, v;   // for 8-connexity
    int L = int(R.x/2.),
        x = int (u.x) - L,
        t = (iFrame/256)%4;                                   // hack to emulate a 1024 white noise texture

  if (iFrame%2==0) {
    O-=O;
    if      (x==  0) O.r = 1.;                                // inject texture on right subwindow column
    else if (x== -1) O.w = step( g * texelFetch(iChannel1, ivec2(P)%256 , 0)[t] , 1. ); // white noise
    else if (x==L-1) O.w = step( g * texelFetch(iChannel2, ivec2(P)%1024, 0).x  , 1. ); // blue noise
    else O = T(u+vec2(1,0));                                  // scroll      
    u.x++;
  }
  else O = T(u);

    O.yz = vec2(0);
    if (u.y==.5) O.yz = vec2(1);                              // seed fluid for percolation i, .yz
    else 
        if(T(u).w > 0. &&  abs(x) > 1  ) {
#define pull(l,x,y)  v = T(u+vec2(x,y)).l,                    /* pull contrib from(x,y) */ \
                     O.l = v==0. ? O.l : O.l==0. ? v : min(O.l, v)

            for( int i=0; i<9; i+=i==3?2:1 ) pull(y, i%3-1, i/3-1 );  // 8-connexity layer in y
            pull(z, 0,-1); pull(z, -1,0); pull(z, 1,0); pull(z, 0,1); // 4-connexity layer in z
            O.yz += step(1.,O.yz);                            // age during propagation
        }
}
