float hash( float n )
{
	return fract((n)*0.5453);
}

float noise( in vec2 x )
{
	vec2 p = floor(x);
	vec2 f = fract(x);
    f = f*f*(3.0-2.0*f);
    float n = p.x + p.y*57.0;
    float hashn = hash(n+  0.0);
   	float res = mix(mix( hashn, hash(n+  1.0),f.x), mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y);
   	return res;
}

float fbm( vec2 p )
{
    	float f = 0.0;
    	f += 1.20000*noise( p ); p = p*2.03;
    	f += 0.35000*noise( p ); //p = m*p*1.03;
    	//f += 0.12500*noise( p ); //p = m*p*2.01;
    	//f += 0.06250*noise( p ); p = m*p*2.04;
    	//f += 0.03125*noise( p );
    	return f/0.984375;
}

float thing(vec2 pos) 
{
	vec2 p = pos;
	pos.x = fract(pos.x + /*fbm(p*0.9)*/ +.5)-0.5;
	pos.y = fract(pos.y + fbm(p*0.9) +.5)-0.5;

	pos = abs(pos);
	float r = sqrt(pos.x*pos.y * 2.5) * 2.;
	return clamp(r-.35, 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
	vec2 position = ( fragCoord.xy / iResolution.xy );
	vec2 world = position * 75.0;
	//world.x *= iResolution.x / iResolution.y;
	float shade = thing(world);
    vec4 color = vec4(0.5, 0.5, 0.4, 1.0);

	fragColor = mix(vec4(0.17, 0.17, 0.17, 1.0), color, shade );
}
