const float intensity = 10.0;

float gray(vec4 n)
{
	return n.r * 0.299 + n.g * 0.587 + n.b * 0.114;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy/iResolution.xy;

	vec2 d = 1.0/iResolution.xy;

	vec2 left_coord = clamp(vec2(uv.xy + vec2(-d.x , 0.0)),0.0,1.0);
	vec2 right_coord = clamp(vec2(uv.xy + vec2(d.x ,  0.0)),0.0,1.0);
	vec2 above_coord = clamp(vec2(uv.xy + vec2( 0.0,d.y)),0.0,1.0);
	vec2 below_coord = clamp(vec2(uv.xy + vec2( 0.0,-d.y)),0.0,1.0);

	vec2 lefta_coord = clamp(vec2(uv.xy + vec2(-d.x , d.x)),0.0,1.0);
	vec2 righta_coord = clamp(vec2(uv.xy + vec2(d.x , d.x)),0.0,1.0);
	vec2 leftb_coord = clamp(vec2(uv.xy + vec2(-d.x , -d.x)),0.0,1.0);
	vec2 rightb_coord = clamp(vec2(uv.xy + vec2(d.x , -d.x)),0.0,1.0);


	vec4 color = texture(iChannel0, uv);
	vec4 colorL = texture(iChannel0, left_coord);
	vec4 colorR = texture(iChannel0, right_coord);
	vec4 colorA = texture(iChannel0, above_coord);
	vec4 colorB = texture(iChannel0, below_coord);

	vec4 colorLA = texture(iChannel0, lefta_coord);
	vec4 colorRA = texture(iChannel0, righta_coord);
	vec4 colorLB = texture(iChannel0, leftb_coord);
	vec4 colorRB = texture(iChannel0, rightb_coord);

	float gx = (-1.0 * gray(colorLA)) + (-2.0 * gray(colorL)) + (-1.0 * gray(colorLB)) + (1.0 * gray(colorRA)) + (2.0 * gray(colorR)) + (1.0 * gray(colorRB));
	float gy = (1.0 * gray(colorLA)) + (2.0 * gray(colorA)) + (1.0 * gray(colorRA)) + (-1.0 * gray(colorRB)) + (-2.0 * gray(colorB)) + (-1.0 * gray(colorLB));

	float bright = pow(gx*gx + gy*gy,0.5);
	vec4 final = color * bright;

	final = final * intensity;

	fragColor = final;
}
