const float side = 70.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Algorithm from 'The New Turing Omnibus' Ch 1 Wallpaper.
	vec2 p = vec2(iTime/100.0) + fragCoord*(side/100.0);
    float c = round(dot(p, p));

    fragColor = (mod(c, 2.0) > 0.0001) ? vec4(0.0) : vec4(1.0);
}
